mapper.select:
select 

,

from sys_menu
where menuModule = #{menumodule,jdbcType=VARCHAR}
  and menuPrivilege = #{menuprivilege,jdbcType=VARCHAR}

mapper.delete:
delete from sys_menu
where menuModule = #{menumodule,jdbcType=VARCHAR}
  and menuPrivilege = #{menuprivilege,jdbcType=VARCHAR}

mapper.insert:
insert into sys_menu (menuModule, menuPrivilege, sort, 
  menuName, title, url, 
  target, icon, remark
  )
values (#{menumodule,jdbcType=VARCHAR}, #{menuprivilege,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}, 
  #{menuname,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{target,jdbcType=VARCHAR}, #{icon,jdbcType=VARCHAR}, #{remark,jdbcType=LONGVARCHAR}
  )

mapper.update:
update sys_menu
set sort = #{sort,jdbcType=INTEGER},
  menuName = #{menuname,jdbcType=VARCHAR},
  title = #{title,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR},
  target = #{target,jdbcType=VARCHAR},
  icon = #{icon,jdbcType=VARCHAR},
  remark = #{remark,jdbcType=LONGVARCHAR}
where menuModule = #{menumodule,jdbcType=VARCHAR}
  and menuPrivilege = #{menuprivilege,jdbcType=VARCHAR}

update sys_menu
set sort = #{sort,jdbcType=INTEGER},
  menuName = #{menuname,jdbcType=VARCHAR},
  title = #{title,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR},
  target = #{target,jdbcType=VARCHAR},
  icon = #{icon,jdbcType=VARCHAR}
where menuModule = #{menumodule,jdbcType=VARCHAR}
  and menuPrivilege = #{menuprivilege,jdbcType=VARCHAR}
