mapper.select:
select 

,

from sys_popedom
where popedomModule = #{popedommodule,jdbcType=VARCHAR}
  and popedomPrivilege = #{popedomprivilege,jdbcType=VARCHAR}

mapper.delete:
delete from sys_popedom
where popedomModule = #{popedommodule,jdbcType=VARCHAR}
  and popedomPrivilege = #{popedomprivilege,jdbcType=VARCHAR}

mapper.update:
update sys_popedom
set sort = #{sort,jdbcType=INTEGER},
  title = #{title,jdbcType=VARCHAR},
  popedomName = #{popedomname,jdbcType=VARCHAR},
  remark = #{remark,jdbcType=LONGVARCHAR}
where popedomModule = #{popedommodule,jdbcType=VARCHAR}
  and popedomPrivilege = #{popedomprivilege,jdbcType=VARCHAR}

update sys_popedom
set sort = #{sort,jdbcType=INTEGER},
  title = #{title,jdbcType=VARCHAR},
  popedomName = #{popedomname,jdbcType=VARCHAR}
where popedomModule = #{popedommodule,jdbcType=VARCHAR}
  and popedomPrivilege = #{popedomprivilege,jdbcType=VARCHAR}
