mapper.delete:
delete from sys_popedom_privilege
where roleId = #{roleid,jdbcType=VARCHAR}
  and popedomModule = #{popedommodule,jdbcType=VARCHAR}
  and popedomPrivilege = #{popedomprivilege,jdbcType=VARCHAR}

mapper.insert:
insert into sys_popedom_privilege (roleId, popedomModule, popedomPrivilege
  )
values (#{roleid,jdbcType=VARCHAR}, #{popedommodule,jdbcType=VARCHAR}, #{popedomprivilege,jdbcType=VARCHAR}
  )
